   message :  success ,
   code : 0,
   data : {
     list : [
      {
         id : 2,
         questId : 1, // 代审核的任务is
         questTitle :  quest1 , // 代审核的任务的标题
         totAmt : 5, // 代审核总数量
         leftAmt : 5, // 代审核剩余数量
         empId : 1, // 申请者用户id
         empUsername :  hanxinxin , // 申请者用户名
         createdTime :  2015-10-15 16:00:00  // 申请时间
      }
    ],
     resultCount : 1
  },
   ok : true
}

